<script>
import UiFooter from "@/components/ui-footer.vue";
export default {
  components: { UiFooter },
  data() {
    return {
      document: null,
      modal: false,
      isMobile: false,
    };
  },
  created() {
    if (process.client) {
      const isAdr = this.$route.query?.type;

      if (isAdr !== "addresat" && !this.$store.getters.getAuth) {
       this.$router.push({
          path: "/document/signin",
          query: { ...this.$route.query, link: window.location.href },
        });
      }
    }
  },
  computed: {
    createdAt() {
      const date = new Date(this.document.createdAt);

      const monthNames = [
        "Jan",
        "Feb",
        "Mar",
        "Apr",
        "May",
        "Jun",
        "Jul",
        "Aug",
        "Sep",
        "Oct",
        "Nov",
        "Dec",
      ];
      const month = monthNames[date.getMonth()];
      const day = date.getDate();
      const year = date.getFullYear();

      const formattedDate = month + " " + day + ", " + year;

      return formattedDate;
    },
  },
  async mounted() {
    try {
      this.document = (await this.$axios.get("/document")).data;

      if (!this.document) {
        this.$router.push('/')
      }
    } catch (error) {
      console.error(error);
    }

    this.isMobile = window.innerWidth <= 600;
  },
  methods: {
    async deleteDoc() {
      if (this.loading) {
        this.$toast.warning("Идет загрузка");
        return;
      }

      this.loading = true;

      try {
        await this.$axios.delete("/document");

        this.$toast.success("Документ успешно удален");
        window.location.reload();
      } catch (error) {
        this.$toast.error(error?.response?.data?.message);
      } finally {
        this.loading = false;
      }
    },
    async handleFileChange(e) {
      const file = e.target.files?.[0];

      const formData = new FormData();
      formData.append("pdf_file", file);

      try {
        this.document = (
          await this.$axios.put("/document", formData)
        ).data?._doc;

        this.$toast.success("Документ успешно заменен");

        this.modal = false;
      } catch (error) {
        console.log(error);
      }
    },
  },
};
</script>

